function getIpAddresses(interfaces) {
    const reg = /^(wlan|usb|eth|en)/;
    const addresses = {};
    for (let iface in interfaces) {
        for (let i = 0; i < interfaces[iface].length; i++) {
            let address = interfaces[iface][i];
            /* Ipv4 & æŽ’é™¤å†…éƒ¨æŽ¥å£ & åŒ¹é…å½“å‰ä¼˜å…ˆçº§çš„ç½‘å£åç§° */
            var matches = iface.match(reg);
            if (
                matches &&
                matches[1] &&
                address.family === "IPv4" &&
                !address.internal
            ) {
                addresses[matches[1]] = address.address;
            }
        }
    }
    return addresses;
}
context.set("getIpAddresses", getIpAddresses);

