// Constants
const DEFAULT_CENTER_X = 320;   // Default center X coordinate
const DEFAULT_CENTER_Y = 320;   // Default center Y coordinate
const THROTTLE_INTERVAL = 300;  // Reduced throttle interval for more responsive control
const DEAD_ZONE = 5;          // Increased dead zone for better stability
const MAX_SPEED = 2000;          // Maximum movement speed
const MIN_SPEED = 10;          // Minimum movement speed

// Camera parameters for smoother motion
const HORIZONTAL_FOV = 60;     // Horizontal field of view in degrees
const VERTICAL_FOV = 40;       // Vertical field of view in degrees
const ANGLE_SCALE_FAR = 0.4;   // Scale factor for targets far from center
const ANGLE_SCALE_NEAR = 0.2;  // Scale factor for targets near center
const MAX_ANGLE = 15;          // Reduced maximum angle for more precise control
const TRANSITION_ZONE = 10;   // Pixel distance for transition between far and near scaling

// PID control parameters
const KP = 0.8;                // Proportional gain
const KI = 0.05;               // Integral gain
const KD = 0.2;                // Derivative gain
const MAX_INTEGRAL = 10;       // Maximum integral value to prevent windup

// Convert pixel offset to angle with adaptive scaling
const pixelToAngle = (pixelOffset, fov, resolution) => {
    // Calculate base angle using tangent relationship
    const fovRadians = (fov * Math.PI) / 180;
    const angleRadians = Math.atan(pixelOffset * 2 * Math.tan(fovRadians / 2) / resolution);
    const baseAngle = (angleRadians * 180 / Math.PI);

    // Apply adaptive scaling based on distance from center
    const absOffset = Math.abs(pixelOffset);
    let scale;
    if (absOffset <= DEAD_ZONE) {
        return 0; // Inside dead zone, no movement
    } else if (absOffset <= TRANSITION_ZONE) {
        // Linear interpolation between near and far scaling factors
        const t = (absOffset - DEAD_ZONE) / (TRANSITION_ZONE - DEAD_ZONE);
        scale = ANGLE_SCALE_NEAR + t * (ANGLE_SCALE_FAR - ANGLE_SCALE_NEAR);
    } else {
        scale = ANGLE_SCALE_FAR;
    }

    return baseAngle * scale;
};

// Calculate speed based on offset with exponential curve for better control
const calculateSpeed = (offset, maxOffset) => {
    const normalizedOffset = Math.abs(offset) / maxOffset;
    // Exponential curve gives more precise control for small movements
    const speed = Math.round((Math.pow(normalizedOffset, 1.5) * (MAX_SPEED - MIN_SPEED)) + MIN_SPEED);
    return Math.min(Math.max(speed, MIN_SPEED), MAX_SPEED);
};

// Apply PID control to angle offset
const applyPIDControl = (offset, axis) => {
    // Get previous values from context
    const prevError = context.get(`prev_error_${axis}`) || 0;
    const integral = context.get(`integral_${axis}`) || 0;

    // Calculate PID terms
    const error = offset;
    const newIntegral = Math.max(-MAX_INTEGRAL, Math.min(MAX_INTEGRAL, integral + error));
    const derivative = error - prevError;

    // Calculate PID output
    const output = (KP * error) + (KI * newIntegral) + (KD * derivative);

    // Store values for next iteration
    context.set(`prev_error_${axis}`, error);
    context.set(`integral_${axis}`, newIntegral);

    return output;
};

// Calculate angle offset with dead zone, limits and PID control
const calculateAngleOffset = (offset, fov, resolution, axis) => {
    if (Math.abs(offset) <= DEAD_ZONE) {
        // Reset integral when in dead zone to prevent drift
        context.set(`integral_${axis}`, 0);
        return 0;
    }

    const angle = pixelToAngle(offset, fov, resolution);
    const pidAngle = applyPIDControl(angle, axis);

    return Math.max(-MAX_ANGLE, Math.min(MAX_ANGLE, pidAngle));
};

