// Initialize variables
let totalSize = 0;       // Total space size in GB
let totalUsed = 0.256;       // Used space in GB
let totalAvailable = 0;   // Available space in GB

// Updated regex to match both MB and GB, and all filesystem types
let regex = /(\S+)\s+([\d.]+)([MKG]?)\s+([\d.]+)([MKG]?)\s+([\d.]+)([MKG]?)\s+(\d+)%/;

// Function to convert MB to GB
function mbToGb(value, unit) {
    switch (unit) {
        case 'G':
            return value;
        case 'M':
            return value / 1024;
        case 'K':
            return value / 1024 / 1024;
        default:
            return 0;
    }
}

// Iterate through each line and sum the values
for (let line of lines) {
    let match = line.match(regex);

    if (match && (match[1] === "/dev/root" || match[1] === "/dev/mmcblk0p6")) {
        // Extract values and units
        let size = parseFloat(match[2]);
        let sizeUnit = match[3];
        let used = parseFloat(match[4]);
        let usedUnit = match[5];
        let available = parseFloat(match[6]);
        let availUnit = match[7];

        // Convert all values to GB
        totalSize += mbToGb(size, sizeUnit);
        totalUsed += mbToGb(used, usedUnit);
        totalAvailable += mbToGb(available, availUnit);
    }
}
// Format the results to two decimal places
// totalSize = totalSize.toFixed(2);         
totalUsed = totalUsed.toFixed(2);       
totalAvailable = totalAvailable.toFixed(2); 
totalSize = (Number(totalUsed) + Number(totalAvailable)).toFixed(2);         

// Calculate used and free percentages
let usedPercentage = ((totalUsed / totalSize) * 100).toFixed(2);
let freePercentage = ((totalAvailable / totalSize) * 100).toFixed(2);

// Create different messages for each output
let output1 = { payload: totalSize };           // Total size in GB
let output2 = { payload: totalUsed };            // Used space in GB
let output3 = { payload: totalAvailable };       // Available space in GB
let output4 = { payload: usedPercentage };       // Used percentage
let output5 = { payload: freePercentage };       // Free percentage

// Return all five outputs as an array
return [output1, output2, output3, output4, output5];

