const THROTTLE_INTERVAL = 80; //Minimum sending interval (ms)

// Get last send time from context
let lastSendTime = context.get('lastSendTime') || 0;

// Get selected bounding box
let selectedBox = msg.payload.selectedBox;
if (selectedBox === false) {
    return null;
}

// Constant definitions
const IMAGE_CENTER_X = 320;
const IMAGE_CENTER_Y = 321;
const SPEED_CONFIG = {
    MAX_SPEED: 20,
    MIN_SPEED: 1,
    MAX_ERROR: 320
};

// Calculate center point error of the target box
const boxCenterX = selectedBox[0];
const boxCenterY = selectedBox[1];
const errorX = boxCenterX - IMAGE_CENTER_X;
const errorY = boxCenterY - IMAGE_CENTER_Y;

const calculateSpeed = (error) => {
    const { MAX_SPEED, MIN_SPEED, MAX_ERROR } = SPEED_CONFIG;

    // Use square function to reduce speed when error is small
    let speed = Math.round((Math.pow(Math.abs(error), 2) / Math.pow(MAX_ERROR, 2)) * (MAX_SPEED - MIN_SPEED)) + MIN_SPEED;

    // Clamp speed between MIN_SPEED and MAX_SPEED
    return Math.min(Math.max(speed, MIN_SPEED), MAX_SPEED);
};

// Generate control command
const generateControl = () => {
    const speedX = calculateSpeed(errorX);
    const speedY = calculateSpeed(errorY);

    // Calculate yaw angle
    let yaw_angle;
    if (errorX < -8) {
        //if object is on the left, gimbal needs to go right
        yaw_angle = 260;
    } else if (errorX > 8) {
        //if object is on the right, gimbal needs to go left
        yaw_angle = 80;
    } else {
        //gimbal remains in the middle
        yaw_angle = 180;
    }

    // Calculate pitch angle
    let pitch_angle;
    if (speedY < -3) {
        //if object is on the top, gimbal needs to go up
        pitch_angle = 10;
    } else if (speedY > 3) {
        //if object is on the botttom, gimbal needs to go down
        pitch_angle = 170;
    } else {
        //gimbal remains in the middle
        pitch_angle = 90;
    }
    //this is a really rought example just to demonstrate the effect,
    //you can use better tracking algorithm to play with gimbal.

    return {
        yaw_angle,
        yaw_speed: speedX,
        pitch_angle,
        pitch_speed: speedY
    };
};

// Check if ready to send
const now = Date.now();
if (now - lastSendTime >= THROTTLE_INTERVAL) {
    // Update send timestamp
    context.set('lastSendTime', now);

    // Generate and send control command
    const control = generateControl();
    return { payload: control };
} else {
    // Command dropped due to throttle interval
    node.warn(`Command dropped, time since last send: ${now - lastSendTime}ms`);
    return null;
}

