// Constants
const DEFAULT_CENTER_X = 320;   // Default center X coordinate
const DEFAULT_CENTER_Y = 320;   // Default center Y coordinate
const THROTTLE_INTERVAL = 200;  // Throttle interval in ms
const DEAD_ZONE = 8;           // Dead zone in pixels
const MAX_SPEED = 80;          // Maximum movement speed
const MIN_SPEED = 10;          // Minimum movement speed

// Camera parameters for smoother motion
const HORIZONTAL_FOV = 45;     // Horizontal field of view in degrees
const VERTICAL_FOV = 30;       // Vertical field of view in degrees
const ANGLE_SCALE = 0.5;       // Additional scale factor to reduce angle
const MAX_ANGLE = 20;          // Maximum angle offset in degrees

// Convert pixel offset to angle
const pixelToAngle = (pixelOffset, fov, resolution) => {
    const fovRadians = (fov * Math.PI) / 180;
    const angleRadians = Math.atan(pixelOffset * 2 * Math.tan(fovRadians / 2) / resolution);
    return (angleRadians * 180 / Math.PI) * ANGLE_SCALE;
};

// Calculate speed based on offset
const calculateSpeed = (offset, maxOffset) => {
    const speed = Math.round((Math.pow(Math.abs(offset), 2) / Math.pow(maxOffset, 2)) * (MAX_SPEED - MIN_SPEED)) + MIN_SPEED;
    return Math.min(Math.max(speed, MIN_SPEED), MAX_SPEED);
};

// Calculate angle offset with dead zone and limits
const calculateAngleOffset = (offset, fov, resolution) => {
    if (Math.abs(offset) <= DEAD_ZONE) return 0;

    const angle = pixelToAngle(offset, fov, resolution);
    return Math.max(-MAX_ANGLE, Math.min(MAX_ANGLE, angle));
};

// Get selected bounding box
const selectedBox = msg.payload.selectedBox;
if (!selectedBox) return null;

// Get resolution or use defaults
const resolution = msg.payload.resolution || [DEFAULT_CENTER_X * 2, DEFAULT_CENTER_Y * 2];
const [imageCenterX, imageCenterY] = resolution.map(dim => Math.round(dim / 2));

// Calculate center point offsets
const [boxCenterX, boxCenterY] = selectedBox;
const offsetX = boxCenterX - imageCenterX;
const offsetY = boxCenterY - imageCenterY;

// Generate control data
const generateControl = () => ({
    yaw_offset: Math.round(-calculateAngleOffset(offsetX, HORIZONTAL_FOV, resolution[0]) * 100) / 100,
    yaw_speed: calculateSpeed(offsetX, imageCenterX),
    pitch_offset: Math.round(calculateAngleOffset(offsetY, VERTICAL_FOV, resolution[1]) * 100) / 100,
    pitch_speed: calculateSpeed(offsetY, imageCenterY)
});

// Throttle control output
const now = Date.now();
const lastSendTime = context.get('lastSendTime') || 0;

if (now - lastSendTime >= THROTTLE_INTERVAL) {
    context.set('lastSendTime', now);
    return { payload: generateControl() };
}

return null;

//When manually controlling gimbal, de-select auto tracking.

